code_comparison.code:

if (
    $expr1
    <em>&& </em>$expr2
    <em>&& </em>$expr3
) {
    // if body
} elseif (
    $expr4
    <em>&& </em>$expr5
) {
    // elseif body
}


if (
    $expr1<em> &&
    </em>$expr2
    <em>&& </em>$expr3
) {
    // if body
} elseif (
    $expr4<em>&&
    </em>$expr5
) {
    // elseif body
}
